mapper.select:
select id, title, summary, view_count as viewCount, comment_count as commentCount, like_count as likeCount, create_time as createdAt, update_time as updatedAt from blog.articles
AND (title LIKE CONCAT('%', #{keyword}, '%') OR content LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY ${sort} ${order}
ORDER BY create_time DESC
LIMIT #{offset}, #{pageSize}
